  PRIMARY KEY (`cid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='美食表' AUTO_INCREMENT=13 ;

--
-- 转存表中的数据 `tp_cases`
--

INSERT INTO `tp_cases` (`cid`, `cname`, `ccity`, `cinfo`, `cbimg`, `aname`, `ctime`) VALUES
(1, '灌汤包子', '西安', '灌汤包子用料考究，制作精细。它以精粉烫面制皮坯，选用肋条肉为馅心，用鲜骨髓汤打馅，配以十多种上等调料佐味。包子鲜香肉嫩、皮簿筋软、外形玲珑剔透、汤汁醇正浓郁、入口油而不腻。', '', 'zhang', '2017-05-16 20:04:00'),
(2, '粉汤羊血', '西安', '粉汤羊血由制血、配调料和泡馍三个步骤精制而成。食时配以粉丝、香菜等辅料。羊血鲜嫩、入口光润、诸味协调、辣香扑鼻。因所用调料多系温中健胃和芳香开窍的中药，故而特别受年老胃弱的顾客青睐。', '', 'lili', '2017-05-16 20:04:00'),
(3, '钱钱饭', '延安', '钱钱饭由粗粮精制。原料为黑豆，经过浸泡，放在碾上压成片，形如铜钱，所以百姓称“钱钱饭”。豆钱二成、米八成，对掺起来，逐次下锅，待豆钱半熟时，再放小米。参考价格0.5元/碗。', '', 'lili', '2017-05-16 20:06:00'),
(4, '泡油糕', '咸阳', '泡油糕也被称作“泡泡油糕”，是三原县很有名气的传统小吃，其渊源可上溯至唐代韦巨源的烧尾宴名点“见见消”（油浴饼）。泡油糕馅是用白糖、黄桂、玫瑰、桃仁、熟面拌成，而原料面粉是用开水、大油烫熟的。用面包好馅料放到油锅里炸，等面上一起泡便捞出来。成品色泽金黄油亮，糕面蓬松，发有大泡，吃起来绵软甜香，入口即消。', '', 'lili', '2017-05-16 20:07:00'),
(5, '仔仔11', '', '放入高汤然后11', '', 'lili', '0000-00-00 00:00:00'),
(10, '贾三灌汤包', '西安', '灌汤包在西安赫赫有名，其中的翘楚就是贾三灌汤包。先来说说这贾三，可是西安的大名人！通过25年潜心研究创作出了独具西安风味的灌汤包。灌汤包用料可是极考究的。它以精粉烫面制皮，选用肋条肉为馅心，用鲜骨髓汤打馅，配以十多种上等调料佐味。包子鲜香肉嫩、皮簿筋软、外形玲珑剔透、汤汁醇正浓郁、入口油而不腻。', '', 'zhang', '2017-05-17 00:00:00'),
(11, '炕炕馍', '安康', '又叫芝麻饼，是一种圆形或长方形的烙饼。圆形似满月，当地叫作“圆炕炕”，碗口大小，边厚中薄；长形似神牌，叫做长炕炕。馍上面粘满芝麻，饼上每隔指许，剁有似断非断的刀印。口感酥香，不干不硬，不顽不软，是当地人最经常吃的食品之一。', '', 'zhang', '2017-05-17 00:00:00'),
(12, '哨子面', '宝鸡', '倍香！', '', 'jiaojiao', '2017-06-07 00:00:00');

