INSERT INTO `tp_hotel` (`hid`, `hname`, `hcity`, `hphone`, `hlevel`, `hroom`, `haddress`, `hinfo`, `hbimg`, `aname`, `hctime`) VALUES
(17, '宝鸡建国饭店', '宝鸡', '0917-3856666', 'AAAAA', '', '金台区行政东路南段', '宝鸡建国饭店是一家由“西北有色七一七总队”投资兴建的一家多功能豪华型酒店。饭店位于宝鸡市行政中心东路南段，南依渭水远眺秦岭，北望蟠龙塬，西邻行政中心广场，交通便利，周边配套设施齐全。饭店总体建筑面积5.6万平米，所有区域WIFI覆盖。饭店拥有267套装修精美的客房，为您带来全新的休憩体验；精品屋、商务中心为您提供各种放松方式。', './Public/IMGS/hotel/59207b4754f27.jpg', 'lili', '2017-05-21 00:00:00'),
(5, '阳光大酒店', '西安', '029-1239120', 'AAAA', '查看详情', '陕西省西安市火车站附近', '全国连锁，质量有保障！', './Public/IMGS/hotel/593634fee7183.jpg', 'lili', '2017-06-06 00:00:00'),
(6, '龙嘉国际酒店', '宝鸡', '0917-1190201', 'AAAAA', '', '陕西省宝鸡市火车站西100米', '宝鸡嘉隆国际酒店是一座现代化新型酒店。地处火车站西侧的经一路与文化路交汇处金角位置。周边商业繁荣，人气旺盛，交通便捷，配套完善，是不可多得的商务平台，休闲胜地。', './Public/IMGS/hotel/59207bc4e4555.jpg', 'lili', '2017-05-21 00:00:00'),
(7, '凤凰大酒店', '西安', '0917-2019014', 'AAA', '', '陕西省宝鸡市凤翔县西区', '原身是凤翔党校', './Public/IMGS/hotel/593634dfc1e4a.jpg', 'lili', '2017-06-06 00:00:00'),
(8, '西安天朗时代大酒店', '西安', '029-6797009', 'AAAAA', '', '莲湖区环城西路北段360号(玉祥门外,近西大街)', '西安天朗时代大酒店是由天朗地产集团按照国际五星级标准投资兴建的西北首家全套房超豪华酒店，共有各类设计匠心独韵，布局人性化的套房350余间，酒店矗立于世界闻名的古城墙之畔，扼守古城西大门交通要道，地理位置十分优越，穿梭西安各主要商务区只需数分钟车程，往国际机场亦仅三十分钟。 ', './Public/IMGS/hotel/593634c07f239.jpg', 'lili', '2017-06-06 00:00:00'),
(9, '魔方布丁酒店', '西安', '029-88255775', 'AAAAA', '', '西安市高新区科技二路中段(文理学院北门西侧100米)', '布丁酒店连锁（西安科技二路店）位于西安高新开发区，东邻西部赛格电脑商城、大雁塔；南邻西安大学城；西邻高新软件园；北邻西安电子科技大学。地处南二环与南三环之间，步行至西部电子广场、人人乐、步行街，只需5分钟，有多条公交路线可直达火车站、城西客运站、城南客运站，距西安咸阳国际机场35公里，出行便捷。', './Public/IMGS/hotel/5936349f8c787.jpg', 'lili', '2017-06-06 00:00:00'),
(10, '大荔东府桃园酒店', '西安', '0913-3757666', 'AAA', '', '商贸大厦与花城路十字西南角', '酒店位于大荔县商贸大道与花城路十字，交通便利，酒店配有中餐厅，宴会厅，停车场等设施设备，服务热情，欢迎入住。', './Public/IMGS/hotel/5936347c5b511.jpg', 'lili', '2017-06-06 00:00:00'),
(11, '宝鸡宝陵大酒店', '宝鸡', '0917-3252308', 'AAA', '', '渭滨区汉中路205号（经一路步行街与汉中路交叉口）', '宝鸡宝陵大酒店位于经一路步行街与汉中路交叉口，邻近银座大厦、天下汇国际购物中心，地理位置优越，交通便利。酒店拥有各类客房，房间设施齐全，是您商务、旅游的理想居所。', './Public/IMGS/hotel/59207c7a1dc9c.jpg', 'lili', '2017-05-21 00:00:00'),
(16, '万福国际酒店', '宝鸡', '0917-3850099', 'AAAAAAA', '', '高新区滨河路66号(滨河路与蟠龙路交叉口南行约100米路西)', ' 宝鸡万福七星国际酒店是由中国建筑西北设计院按照五星级酒店标准设计建造，集住宿、餐饮、会议、娱乐、商务为一体的国际型酒店。酒店总建筑面积8.6万平方米，内设多种套型、温馨舒适的客房500余间；大、中、小型会议室20余间；中餐、西餐、风味餐包厢70余间；室内配有游泳池、水疗中心、KTV音乐包房、酒吧、咖啡厅、羽毛球馆、瑜伽馆、健身房、美容、按摩室，可容纳千人的大型宴会厅以及多功能厅。', './Public/IMGS/hotel/592079b582de6.jpg', 'lili', '2017-05-21 00:00:00'),
(13, '西安皇冠假日酒店', '西安', '029-88668888', 'AAAAA', '', '碑林区朱雀路中段1号( 小寨地区 近小雁塔 高新区 市区及曲江商圈交 )', '地理位置得天独厚 交通便捷 由国际品牌洲际集团管理', './Public/IMGS/hotel/592078655eb24.jpg', 'zhang', '2017-05-21 00:00:00');

