--
-- 表的结构 `tp_line`
--

CREATE TABLE IF NOT EXISTS `tp_line` (
  `xid` int(4) NOT NULL AUTO_INCREMENT COMMENT '线路id',
  `xname` varchar(32) NOT NULL COMMENT '线路名称',
  `xtour` varchar(200) NOT NULL COMMENT '途径景点',
  `xcity` varchar(200) NOT NULL COMMENT '途径城市',
  `xbimg` varchar(100) NOT NULL COMMENT '线路图片',
  `xtime` char(50) NOT NULL COMMENT '旅游耗时',
  `xprice` varchar(80) NOT NULL COMMENT '旅游预算',
  `xinfo` varchar(255) NOT NULL COMMENT '线路介绍',
  `aname` varchar(32) NOT NULL COMMENT '发布人',
  `xctime` datetime NOT NULL COMMENT '最后操作时间',
  PRIMARY KEY (`xid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='路线表' AUTO_INCREMENT=27 ;

--
-- 转存表中的数据 `tp_line`
--

INSERT INTO `tp_line` (`xid`, `xname`, `xtour`, `xcity`, `xbimg`, `xtime`, `xprice`, `xinfo`, `aname`, `xctime`) VALUES
(1, '西安一日游', '大唐芙蓉园-大雁塔-钟楼', '西安', '', '一天', '682元', '', 'lili', '2017-05-23 17:09:00'),
(2, '延安二日游', '延安黄帝陵景区-延安杨家岭革命旧址-延安王家坪革命旧址-延安枣园革命旧址', '延安', '', '两天', '350元', '', 'lili', '2017-05-23 17:15:00'),
(3, '西安东线一日游', '兵马俑-华清池', '西安', '', '一天', '340元', '', 'zhang', '2017-05-23 17:17:00'),
(4, '宝鸡三日游', '云台山-红石峡-潭瀑峡-圆融寺-太极故里', '宝鸡', '', '三天', '338元', '', 'zhang', '2017-05-23 17:19:00'),
(5, '宝鸡周边一日游', '周至水街-玫瑰庄园-马嵬驿', '宝鸡', '', '一天', '99元', '【宝鸡起止】周至水街 玫瑰庄园 马嵬驿一日游', 'lili', '2017-05-23 17:31:00'),
(6, '宝鸡西安三日游', '人民公园-法门寺-大唐芙蓉园-钟楼-大雁塔-小寨-华山', '宝鸡', '', '三日游', '22221', '玩个遍！！！', 'jiaojiao', '2017-05-31 00:00:00'),
(17, '华山旅游 “西上北下” 经典线路不走回头路', '华山东峰-华山南峰-华山西峰-华山北峰-华山中峰玉女-苍龙岭', '西安,渭南', './Public/IMGS/line/59362dd74079b.jpg', '一日游', '210元', '"西岳"华山一日游：华山风景区，中国五岳之一“西岳”华山 ，"奇险天下第一山" ，以奇、险、俊、秀著称于世。', 'jiaojiao', '2017-06-06 00:00:00'),
(16, '陕西境内三日游', '大唐芙蓉园-渭河-延安白杨林-汉中山丘-安康花园', '咸阳,渭南,延安,汉中,榆林,安康,商洛', './Public/IMGS/line/59362c4284aed.jpg', '三日', '730元', '由北向南陕西旅行！', 'jiaojiao', '2017-06-06 00:00:00'),
(26, '西安一日游', '大唐芙蓉园-大雁塔-钟楼', '西安', './Public/IMGS/line/5936c34663f0a.jpg', '一天', '682元', '来西安尽情感受三秦文化吧！', 'lili', '2017-06-06 00:00:00'),
(18, '西安六日全线游', '兵马俑-华山-法门寺-延安-壶口瀑布', '西安', './Public/IMGS/line/59362e7d34d25.jpg', '六日游', '1590元', '游秦始皇帝陵博物院世界第八大奇迹秦兵马俑；参观唐玄宗李隆基和四大美女杨贵妃爱情别苑“春寒赐浴华清池，温泉水滑洗凝脂”避寒胜地，唐华清宫遗址-—华清池；西安事变旧址--五间厅；攀登以险俊闻名天下的西岳华山之雄峰；去陕北延安看民居窑洞、听黄河壶口瀑布的咆哮、参观红色革命旧址－－枣园、杨家岭；龙年祭祖去黄帝陵；到佛家圣地法门寺为家人祈福安康；到武则天一代女皇的陵墓－－乾陵去探究无字碑的原由……', 'jiaojiao', '2017-06-06 00:00:00'),
(19, '黄帝陵一日游', '黄帝陵', '延安', './Public/IMGS/line/59362f75b271a.jpg', '一日游', '80元', '中华民族历史悠久，光辉灿烂，有五千年的文明史。这五千年的文明史主要从黄帝开国(建国)算起。孙中山的祭黄帝陵词开首就是“中华开国五千年，神州轩辕自古传，创造指南车，平定蚩尤乱，世界文明，唯有我先”。', 'jiaojiao', '2017-06-06 00:00:00'),
(20, '西安北线两日游', '轩辕庙-王家坪革命旧址', '西安,延安', './Public/IMGS/line/59363d239d518.jpg', '两日游', '410元', '革命旧址二日游！', 'jiaojiao', '2017-06-06 00:00:00'),
(21, '宝鸡周边一日游', '周至水街-玫瑰庄园-马嵬驿', '宝鸡', './Public/IMGS/line/59363d7bbb853.jpg', '一天', '99元', '【宝鸡起止】周至水街 玫瑰庄园 马嵬驿一日游', 'jiaojiao', '2017-06-06 00:00:00'),
(22, '天下第一陵乾陵', '天下第一陵乾陵-法门寺', '宝鸡,咸阳', './Public/IMGS/line/59363e25030b6.jpg', '一日游', '280元', '来西安旅游景点推荐：天下第一陵乾陵、法门寺一日游', 'jiaojiao', '2017-06-06 00:00:00'),
(23, '关山牧场秋菊山庄两日游关山两日游', '关山牧场-秋菊山庄', '宝鸡', './Public/IMGS/line/59363ece6091f.jpg', '两日游', '298元', '关山牧场秋菊山庄两日游关山2日跟团游多钱（赠篝火可洗热水澡）', 'jiaojiao', '2017-06-06 00:00:00'),
(24, '云台山三日游', '云台山-红石峡-潭瀑峡', '西安', './Public/IMGS/line/59363f60e4d32.jpg', '三日游', '389元', '【云台山三日游】西安周边爬山、休闲、度假好去处', 'jiaojiao', '2017-06-06 00:00:00');

