--
-- 表的结构 `tp_produces`
--

CREATE TABLE IF NOT EXISTS `tp_produces` (
  `pid` int(4) NOT NULL AUTO_INCREMENT COMMENT '特产编号',
  `pname` varchar(50) NOT NULL COMMENT '特产名称',
  `pcity` varchar(20) NOT NULL COMMENT '产地',
  `pinfo` varchar(255) NOT NULL COMMENT '特产介绍',
  `pbimg` varchar(200) NOT NULL COMMENT '特产图片',
  `aname` varchar(32) NOT NULL COMMENT '发布人',
  `ptime` datetime NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`pid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='特产表' AUTO_INCREMENT=15 ;

--
-- 转存表中的数据 `tp_produces`
--

INSERT INTO `tp_produces` (`pid`, `pname`, `pcity`, `pinfo`, `pbimg`, `aname`, `ptime`) VALUES
(11, '荞麦', '延安', '荞麦是延安传统出口产品之一，主要产区为延安北部各县，有“陕北三宝，荞麦、羊肉、地椒草”的说法。延安荞麦生长期长，颗大质优，磨粉做面食洁白细长，光滑爽口。这种天然食品为粗粮杂粮，不仅味美，而且有一定的保健功效。荞麦浑身是宝，当地还出产优质的荞麦蜜，荞麦皮枕头等制品。', '', 'lili', '0000-00-00 00:00:00'),
(9, '德懋恭水晶饼', '西安', '水晶饼是一种酥皮点心，因其馅晶莹透亮，犹如水晶而得其名。成品皮酥馅足，滋润适口，层次分明，油多不腻，糖重口渗甜，而且具有浓郁的玫瑰和橘饼清香。水晶饼面色金黄，四周雪白，素有“金底银帮鼓鼓腔，红色印章盖中央”的赞誉，被称为“秦点之首”。水晶饼以西安德懋恭生产的最为有名，清末被慈禧太后定为“贡品”，作为陕西的名特食品著称全国。', '', 'lili', '2017-05-17 00:00:00'),
(10, '红枣', '延安', '陕北的红枣闻盛名己久，尤其延安延川县的红枣更负盛名。延川县是大枣的理想适生区，这里产的团枣、条枣、狗头枣果实个大、肉厚味甜。另产脆枣，又名吊牙枣，果实大、皮薄味甜，肉白细脆。当地还加工出产“宝塔牌”红枣酱、金丝蜜枣、醉枣等风味产品。', '', 'lili', '2017-05-17 00:00:00'),
(12, '西乡牛肉干', '汉中', '汉中市西乡县特产的西乡牛肉干，是陕南西乡县传统名贵的清真风味食品，已有数百年的历史。早在140多年前，就由西乡回民寺院阿訇自制自食，后屡经改进加工，成为地方名产。特点是：颜色红润鲜嫩，呈红褐色或咖啡色，精肉呈桃红色，切面带光泽，香味浓郁，味道鲜美，咸淡适中，表面涂上香油，油润光亮，冬季可存放二三十天，贮存、携带、食用方便，为佐餐下酒美食。', '', 'zhang', '2017-05-17 00:00:00'),
(13, '松花变蛋', '汉中', '“变蛋”即皮蛋。西乡松花变蛋因蛋清表皮呈松、柏叶阴影状花朵而得名，已有百余年历史。制作技术独特，别具风味，成品质地透明，松花晶莹，软硬适宜，香而不腻。是制作“拼盘”不可缺少的菜品，单吃口味亦佳。', '', 'zhang', '2017-05-17 00:00:00'),
(14, '黄桂稠酒', '西安', '黄桂稠酒选用优质的糯米和小曲为原料，酿造工艺讲究。在整个酿制过程中，从糯米淘洗到拌曲、装坛、压酒等十多道工序，都有明确的规程。成品酒液中配有中药黄桂，状如牛奶，汁稠醇香，绵甜适口，酒精含量在15%左右。饮用时根椐需要可放在水锅中加热烧开热饮，还可根据口味加入白糖。相传“李白斗酒诗百篇”所饮即为稠酒。', '', 'zhang', '2017-05-17 00:00:00');

