--
-- 表的结构 `tp_tourist`
--

CREATE TABLE IF NOT EXISTS `tp_tourist` (
  `tid` int(4) NOT NULL AUTO_INCREMENT COMMENT '景点id',
  `tname` varchar(32) NOT NULL COMMENT '景点名称',
  `tcity` varchar(32) NOT NULL COMMENT '景点所在城市',
  `tlevel` char(50) NOT NULL COMMENT '景点等级',
  `ttype` varchar(40) NOT NULL COMMENT '景点类型',
  `taddress` varchar(100) NOT NULL COMMENT '景点详细地址',
  `tabstract` varchar(255) NOT NULL COMMENT '景点简介',
  `ticket` varchar(80) NOT NULL COMMENT '门票信息',
  `tbimg` varchar(255) NOT NULL COMMENT '相关的大图片',
  `tline` text NOT NULL COMMENT '公交路线',
  `aname` varchar(32) NOT NULL,
  `tctime` datetime NOT NULL COMMENT '最后操作时间',
  PRIMARY KEY (`tid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=62 ;

--
-- 转存表中的数据 `tp_tourist`
--

INSERT INTO `tp_tourist` (`tid`, `tname`, `tcity`, `tlevel`, `ttype`, `taddress`, `tabstract`, `ticket`, `tbimg`, `tline`, `aname`, `tctime`) VALUES
(51, '野河山', '西安', 'AAAA', '森林', '宝鸡市扶风县涧沟桥旁野河山景区', '贵妃故里野河山生态旅游景区，地处陕西省扶风县北部，距佛教圣地法门寺8公里。', '45元起（省15元）', './Public/IMGS/tour/591d986b34196.jpg', '西安市---西宝高速---乾汤线---野郭路---野河山景区', 'lili', '2017-05-17 00:00:00'),
(56, '延安南泥湾革命旧址', '延安', 'AAAA', '红色旅游', '延安城东南45公里处', '位于延安城东南45公里处。南泥湾是中国共产党军垦事业的发祥地，是南泥湾精神的诞生地。', ' 免费开放', './Public/IMGS/tour/591d6e5ca1d3a.jpg', '在宝塔山下的桥头乘坐去南泥湾班车，参考车费15元。', 'zhang', '2017-05-18 00:00:00'),
(2, '宝鸡法门寺', '西安', 'AAAA', '寺庙', '陕西省宝鸡市扶风县城北10公里处的法门镇', '法门寺位于陕西省扶风县城北十公里的法门镇。始建于东汉末年，发迹于北魏，起兴于隋，鼎盛于唐，被誉为“皇家寺庙”，因安置释迦牟尼佛指骨舍利而成为举国仰望的佛教圣地。', '成人票：￥120', './Public/IMGS/tour/5936c22bf420d.jpg', '公交： 城西客运站坐西安到扶风的班车，再坐班车到法门寺。自驾：西安-福银高速乾陵出口下--（乾陵东门上）乾陵大陵（乾陵南门下）--永泰公主墓--209省道--法门寺。', 'lili', '2017-06-06 00:00:00'),
(22, '灵山', '宝鸡', 'AAAA', '公园', '陕西省宝鸡市凤翔县', '佛教圣地', '无', './Public/IMGS/tour/5936c1eb566fd.jpg', '班车', 'lili', '2017-06-06 00:00:00'),
(23, '曲江乔氏院落', '西安', 'AA', '园林', '曲江一路12号', '看看中原乔氏院落！', '15元', './Public/IMGS/tour/5936c1beeda30.jpg', '班车', 'lili', '2017-06-06 00:00:00'),
(24, '西安临潼骊山', '西安', 'AAAA', '山脉', ' 西安临潼区城南', ' 骊山，位于西安临潼区城南，属秦岭山脉的一个支脉，靠着兵马俑博物馆，最高峰九龙顶海拔 1301.9米，山势逶迤，树木葱茏，远望宛如一匹苍黛色的骏马而得名。骊山也因景色翠秀，美如锦绣，故又名“绣岭”。每当夕阳西下，骊山辉映在金色的晚霞之中，景色格外绮丽，有“骊山晚照”之美誉。', '70元每人次', './Public/IMGS/tour/5936c12827d5c.jpg', '临潼区内设101、102、201、301、826路公交车辆及城区至阎良区等地的公交中巴', 'jiaojiao', '2017-06-06 00:00:00'),
(39, '陕西历史博物馆', '西安', 'AAAA', '红色旅游', ' 陕西省西安市小寨东路91号', '陕西历史博物馆，是位于陕西西安的一座国家级综合性历史类大型博物馆。', '大唐珍宝馆门票20元，壁画馆门票300元', './Public/IMGS/tour/59206d54979e0.jpg', '54', 'zhang', '2017-05-21 00:00:00'),
(45, '东风水库', '宝鸡', 'AAA', '山脉', '陕西省凤翔县柳林镇', '夏天有很多人去钓鱼。', '无', './Public/IMGS/tour/59206c7a993ce.jpg', '专车接送', 'zhang', '2017-05-21 00:00:00'),
(46, '关山牧场', '宝鸡', 'AAA', '森林', '陕西省宝鸡市陇县', '风热', '129', './Public/IMGS/tour/591d9ee01bf2d.jpg', '23', 'zhang', '2017-05-15 00:00:00'),
(47, '大佛寺', '宝鸡', 'AAA', '寺庙', '宝鸡市凤翔县柳林镇大佛寺村', '庙会', '11', './Public/IMGS/tour/591d9e926e026.jpg', '21', 'zhang', '2017-05-15 00:00:00'),
(57, '西安植物园', '西安', 'AAA', '森林', ' 陕西西安市翠华南路17号', '陕西省西安植物园成立于1959年，是西北地区成立最早的植物园和引种保存植物种类最多的科研机构。', '10元', './Public/IMGS/tour/591d9b3f4e226.jpg', ' 乘坐12路、26路、27路、503路终点站下车即到', 'zhang', '2017-05-18 00:00:00'),
(58, '渭南合阳洽川', '渭南', 'AA', '亭台楼阁，公园', '陕西关中平原东部的合阳境内', '“洽川”位于陕西合阳县城东23公里处的洽川镇黄河二级台地上，东西宽3公里，南北长10公里。洽川东临黄河、西依青山，土地肥沃，气候湿润，水源充足，物产丰富，素有“小江南”之美称。 ', '27元', './Public/IMGS/tour/59205734f2d2e.jpg', '班车', 'zhang', '2017-05-20 00:00:00'),
(59, '未央湖游乐园', '西安', 'AAA', '游乐场', '陕西西安市北郊', '西安未央湖游乐园是以集休闲、娱乐为一体的现代大型游乐园，占地近千亩，其中水域面积480亩，是西北最大的人工湖。 　　未央湖游乐园共分为五大区：南区是文化广场区，有大型音乐彩色喷泉、红太阳大舞台、迪斯科广场、未央湖餐饮中心等，是各种文化娱乐的主要活动场所。', '现买', './Public/IMGS/tour/5920585c3a941.jpg', '市内公交', 'zhang', '2017-05-20 00:00:00'),
(60, '汉中红寺湖', '汉中', 'AAA', '山脉，公园', ' 陕西汉中市南郑县红庙镇', '美丽的红寺湖，有如一个天然的硕大的盆景，镶嵌在南郑县西南的绿水青山之中。景区面积约35平方公里，森林覆盖率达882，距中国历史文化名城汉中市25公里，是汉中最具魅力的生态风景旅游区。', '35元', './Public/IMGS/tour/5936c2a81ed67.jpg', '南郑县城上211省道至青树镇', 'lili', '2017-06-06 00:00:00'),
(61, '终南山秦楚古道', '西安', 'AAA', '古镇', ' 陕西省柞水县营盘镇太河', '秦楚古道四季如画、气象万千，正如清代诗人余作梅描写的一样：“太乙钟灵聚此间，天开形势占秦关，诗峰翠锁金城固，一水清流玉带环。”秦楚古道跨越终南山，北麓山势陡峭，易观山景；南麓地势缓长，易享清凉。秀丽俊美，如诗如画的自然风光让人留恋往返。', '50元', '', '西柞高速', 'jiaojiao', '2017-06-07 00:00:00');

