--
-- 表的结构 `tp_user`
--

CREATE TABLE IF NOT EXISTS `tp_user` (
  `uid` int(4) NOT NULL AUTO_INCREMENT,
  `uname` varchar(30) NOT NULL,
  `upwd` char(32) NOT NULL,
  `sex` int(1) NOT NULL DEFAULT '1',
  `uimg` varchar(100) NOT NULL COMMENT '用户头像',
  `uemail` varchar(80) NOT NULL COMMENT '用户邮箱',
  `uctime` datetime NOT NULL COMMENT '申请时间',
  PRIMARY KEY (`uid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='用户表' AUTO_INCREMENT=6 ;

--
-- 转存表中的数据 `tp_user`
